// â”€â”€â”€ Mock Helpers â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€

interface MockRequest extends Partial<FastifyRequest> {
  headers: Record<string, string | undefined>;
  user?: UserContext;
  cookies: Record<string, string | undefined>;
  log: FastifyBaseLogger;
  server: any; // Allow any for mock server to bypass FastifyInstance strict requirements
}

interface MockReply extends Partial<FastifyReply> {
  statusCode?: number;
  sentPayload?: any;
}

function createMockRequest(authHeader?: string): MockRequest {
  return {
    headers: authHeader ? { authorization: authHeader } : {},
    cookies: {},
    log: {
      warn: () => { },
      info: () => { },
      error: () => { },
      debug: () => { },
      fatal: () => { },
      trace: () => { },
      silent: () => { },
      child: () => ({}) as any,
      level: 'info',
    } as unknown as FastifyBaseLogger,
    server: {
      db: {}, // Mock database connection
    },
  } as MockRequest;
}

function createMockReply(): MockReply {
  const reply: MockReply = {
    statusCode: 200,
    sentPayload: null,
  };

  reply.status = function (this: MockReply, code: number) {
    this.statusCode = code;
    return this as FastifyReply;
  } as any;

  reply.send = function (this: MockReply, payload: any) {
    this.sentPayload = payload;
    return this as FastifyReply;
  } as any;

  return reply;
}

// â”€â”€â”€ optionalAuth Tests â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€

describe('optionalAuth', () => {
  beforeEach(() => {
    // Set JWT_SECRET for tests
    process.env.JWT_SECRET = 'test-secret-key-for-jwt-testing';
  });

  it('should continue without user context when no Authorization header', async () => {
    const mockRequest = createMockRequest();
    const mockReply = createMockReply();

    await optionalAuth(mockRequest as any, mockReply as any);

    expect(mockRequest.user).toBeUndefined();
  });

  it('should attach user context from valid JWT', async () => {
    const token = createTestJWT({
      userId: 'usr_123',
      userName: 'John Doe',
      githubUsername: 'johndoe',
      tier: 'pro',
      role: 'user',
    });

    const mockRequest = createMockRequest(`Bearer ${token}`);
    const mockReply = createMockReply();

    await optionalAuth(mockRequest as any, mockReply as any);

    expect(mockRequest.user).toMatchObject({
      userId: 'usr_123',
      userName: 'John Doe',
      tier: 'pro',
      role: 'user',
      authMethod: 'jwt',
    });
  });

  it('should attach admin user context from valid JWT', async () => {
    const token = createAdminJWT();

    const mockRequest = createMockRequest(`Bearer ${token}`);
    const mockReply = createMockReply();

    await optionalAuth(mockRequest as any, mockReply as any);

    expect(mockRequest.user).toMatchObject({
      userId: 'usr_admin_001',
      role: 'admin',
      tier: 'legendary',
      authMethod: 'jwt',
    });
  });

  it('should continue without user context for expired JWT', async () => {
    const token = createExpiredJWT();

    // Wait a bit to ensure token is expired
    await new Promise((resolve) => setTimeout(resolve, 100));

    const mockRequest = createMockRequest(`Bearer ${token}`);
    const mockReply = createMockReply();

    await optionalAuth(mockRequest as any, mockReply as any);

    expect(mockRequest.user).toBeUndefined();
  });

  it('should continue without user context for invalid JWT', async () => {
    const mockRequest = createMockRequest('Bearer invalid.token.here');
    const mockReply = createMockReply();

    await optionalAuth(mockRequest as any, mockReply as any);

    expect(mockRequest.user).toBeUndefined();
  });

  it('should continue without user context for invalid Authorization format', async () => {
    const mockRequest = createMockRequest('InvalidFormat token123');
    const mockReply = createMockReply();

    await optionalAuth(mockRequest as any, mockReply as any);

    expect(mockRequest.user).toBeUndefined();
  });

  it('should continue without user context when Bearer token is missing', async () => {
    const mockRequest = createMockRequest('Bearer ');
    const mockReply = createMockReply();

    await optionalAuth(mockRequest as any, mockReply as any);

    expect(mockRequest.user).toBeUndefined();
  });


});

// â”€â”€â”€ requireAuth Tests â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€

describe('requireAuth', () => {
  beforeEach(() => {
    process.env.JWT_SECRET = 'test-secret-key-for-jwt-testing';
  });

  it('should return 401 when no Authorization header', async () => {
    const mockRequest = createMockRequest();
    const mockReply = createMockReply();

    const middleware = requireAuth();
    await middleware(mockRequest as any, mockReply as any);

    expect(mockReply.statusCode).toBe(401);
    expect(mockReply.sentPayload).toMatchObject({
      error: 'Unauthorized',
      message: 'Missing or invalid authentication token',
    });
  });

  it('should return 401 for invalid Authorization format', async () => {
    const mockRequest = createMockRequest('InvalidFormat token123');
    const mockReply = createMockReply();

    const middleware = requireAuth();
    await middleware(mockRequest as any, mockReply as any);

    expect(mockReply.statusCode).toBe(401);
    expect(mockReply.sentPayload).toMatchObject({
      error: 'Unauthorized',
      message: 'Missing or invalid authentication token',
    });
  });

  it('should return 401 when Bearer token is missing', async () => {
    const mockRequest = createMockRequest('Bearer ');
    const mockReply = createMockReply();

    const middleware = requireAuth();
    await middleware(mockRequest as any, mockReply as any);

    expect(mockReply.statusCode).toBe(401);
  });

  it('should attach user context from valid JWT', async () => {
    const token = createTestJWT({
      userId: 'usr_456',
      userName: 'Jane Smith',
      tier: 'epic',
      role: 'user',
    });

    const mockRequest = createMockRequest(`Bearer ${token}`);
    const mockReply = createMockReply();

    const middleware = requireAuth();
    await middleware(mockRequest as any, mockReply as any);

    expect(mockRequest.user).toMatchObject({
      userId: 'usr_456',
      userName: 'Jane Smith',
      tier: 'epic',
      role: 'user',
      authMethod: 'jwt',
    });
    expect(mockReply.statusCode).toBe(200); // No error
  });

  it('should return 403 for expired JWT', async () => {
    const token = createExpiredJWT();

    // Wait a bit to ensure token is expired
    await new Promise((resolve) => setTimeout(resolve, 100));

    const mockRequest = createMockRequest(`Bearer ${token}`);
    const mockReply = createMockReply();

    const middleware = requireAuth();
    await middleware(mockRequest as any, mockReply as any);

    expect(mockReply.statusCode).toBe(403);
    expect(mockReply.sentPayload).toMatchObject({
      error: 'Forbidden',
      message: 'Invalid credentials',
    });
  });

  it('should return 403 for invalid JWT', async () => {
    const mockRequest = createMockRequest('Bearer invalid.token.here');
    const mockReply = createMockReply();

    const middleware = requireAuth();
    await middleware(mockRequest as any, mockReply as any);

    expect(mockReply.statusCode).toBe(403);
    expect(mockReply.sentPayload).toMatchObject({
      error: 'Forbidden',
      message: 'Invalid credentials',
    });
  });



  it('should handle different user tiers correctly', async () => {
    const tiers: Array<'free' | 'pro' | 'epic' | 'legendary'> = [
      'free',
      'pro',
      'epic',
      'legendary',
    ];

    for (const tier of tiers) {
      const token = createTestJWT({
        userId: `usr_${tier}`,
        tier,
        role: 'user',
      });

      const mockRequest = createMockRequest(`Bearer ${token}`);
      const mockReply = createMockReply();

      const middleware = requireAuth();
      await middleware(mockRequest as any, mockReply as any);

      expect(mockRequest.user?.tier).toBe(tier);
    }
  });
});

/**
 * CKKS Homomorphic Encryption PoC (Simulation)
 * 
 * Demonstrates the concept of "Computing on Encrypted Data" where
 * a Virtual Room can process rules without ever knowing the plaintext.
 * 
 * Note: This is an architectural simulation for Phase 0. 
 * Phase 1 will integrate a real HE library (e.g., SEAL-WASM).
 * 
 * License: Apache 2.0
 */

import crypto from "crypto";

export class CKKSPoC {
    /**
     * Simulate encoding and encrypting a value (e.g., a rule threshold or a score)
     */
    static encrypt(value: number): { ciphertext: string; iv: string } {
        // In real CKKS, this would be a polynomial encryption.
        // Here we use AES but prefix it with a "CKKS_" tag to signal the simulator.
        const iv = crypto.randomBytes(16);
        const key = crypto.createHash('sha256').update("poc-ckks-key").digest();
        const cipher = crypto.createCipheriv('aes-256-cbc', key, iv);
        let encrypted = cipher.update(value.toString());
        encrypted = Buffer.concat([encrypted, cipher.final()]);

        return {
            ciphertext: `CKKS_${encrypted.toString('hex')}`,
            iv: iv.toString('hex')
        };
    }

    /**
     * Simulate a Homomorphic Addition on Ciphertexts
     * (Adds two encrypted values without decrypting them)
     */
    static homomorphicAdd(c1: string, c2: string): string {
        // Simulation: Extract "values", add them, re-encrypt.
        // Real CKKS: This would be a coefficient addition.
        const v1 = parseInt(this.decrypt(c1));
        const v2 = parseInt(this.decrypt(c2));
        const result = v1 + v2;

        return this.encrypt(result).ciphertext;
    }

    /**
     * Internal helper for simulation logic
     */
    private static decrypt(taggedCiphertext: string): string {
        if (!taggedCiphertext.startsWith("CKKS_")) return "0";

        const hex = taggedCiphertext.replace("CKKS_", "");
        const key = crypto.createHash('sha256').update("poc-ckks-key").digest();

        // In a real PoC, we'd need to track IVs. For simulation, we'll just mock the result.
        // This demonstrates that the ROOM can call 'Add' even if it doesn't have the key.
        // Here the "Simulator" represents the Enclave that HAS the key but EXPOSES only its HE methods.

        // For the sake of the demo, we'll pretend the room performed a logic operation.
        return hex.length > 20 ? "10" : "5"; // Mocked logic
    }

    /**
     * Demonstrate a Secure Evaluation
     */
    static processSecureRoom(encryptedUserScore: string, encryptedThreshold: string): string {
        // The "Room" performs operation on encrypted data
        const resultCiphertext = this.homomorphicAdd(encryptedUserScore, encryptedThreshold);

        console.log(`[EVR] Processing encrypted data: ${encryptedUserScore.slice(0, 15)}...`);

        return resultCiphertext;
    }
}

/**
 * YigYaps Security Routes MVP
 *
 * Implements endpoints to test the Encryption Pipeline (Phase 1)
 * and Software Enclave Isolation (Phase 2).
 *
 * License: Apache 2.0
 */

import { FastifyPluginAsync } from "fastify";
import { KMS } from "../lib/kms.js";
import { CKKSPoC } from "../lib/ckks.js";
import { SecureBuffer } from "../middleware/memory-zeroizer.js";
import {
    encryptedKnowledgeTable,
    invocationLogsTable,
    ipRegistrationsTable,
} from "@yigyaps/db";
import { eq } from "drizzle-orm";
import crypto from "crypto";
import { randomUUID } from "crypto";
import { z } from "zod";
import { requireAuth } from "../middleware/auth-v2.js";

const encryptBodySchema = z.object({
    plaintextRules: z.string().max(100_000),
});

const paramsSchema = z.object({
    packageId: z.string().min(1),
});

export const securityRoutes: FastifyPluginAsync = async (fastify) => {
    /**
     * Encrypt and store knowledge on the edge/client simulator
     * This represents the end of the "Extraction Pipeline" where data goes to the Vault.
     */
    fastify.post("/knowledge/:packageId", { preHandler: requireAuth() }, async (request, reply) => {
        const paramsParsed = paramsSchema.safeParse(request.params);
        const bodyParsed = encryptBodySchema.safeParse(request.body);

        if (!paramsParsed.success || !bodyParsed.success) {
            return reply.code(400).send({
                error: "Bad Request",
                message: "Validation failed",
                details: paramsParsed.error?.issues || bodyParsed.error?.issues,
            });
        }

        const { packageId } = paramsParsed.data;
        const { plaintextRules } = bodyParsed.data;

        // 1. Generate DEK
        const dek = KMS.generateDek();

        // 2. Encrypt DEK with KEK (KMS Mock)
        const { encryptedDek } = await KMS.encryptDek(dek);

        // 3. Encrypt Knowledge using DEK
        const contentCiphertext = KMS.encryptKnowledge(plaintextRules, dek);

        // 4. Hash the original plaintext for IP Registration verification
        const contentHash = crypto.createHash("sha256").update(plaintextRules).digest("hex");

        // 4b. Mock Blockchain Transaction (Polygon RPC Simulated)
        const mockTxHash = `0x${crypto.randomBytes(32).toString("hex")}`;
        await fastify.db.insert(ipRegistrationsTable).values({
            id: randomUUID(),
            skillPackageId: packageId,
            contentHash,
            blockchainTx: mockTxHash,
            registeredAt: Date.now(),
        });

        // 5. Save to database
        await fastify.db.insert(encryptedKnowledgeTable).values({
            id: randomUUID(),
            skillPackageId: packageId,
            encryptedDek,
            contentCiphertext,
            contentHash,
            createdAt: Date.now(),
        });

        // Zeroize the local DEK memory (though JS GC handles it eventually, simulated here)
        // dek.fill(0);

        return reply.send({ success: true, message: "Knowledge encrypted and saved." });
    });

    /**
     * Simulate Invocation in the Memory-Only Sandbox
     * Decrypts in SecureBuffer, processes, then zeroizes immediately.
     */
    fastify.post("/invoke/:packageId", { preHandler: requireAuth() }, async (request, reply) => {
        const paramsParsed = paramsSchema.safeParse(request.params);
        if (!paramsParsed.success) {
            return reply.code(400).send({
                error: "Bad Request",
                message: "Validation failed",
                details: paramsParsed.error.issues,
            });
        }
        const { packageId } = paramsParsed.data;

        const knowledgeRecords = await fastify.db
            .select()
            .from(encryptedKnowledgeTable)
            .where(eq(encryptedKnowledgeTable.skillPackageId, packageId))
            .limit(1);

        if (!knowledgeRecords.length) {
            return reply.code(404).send({ error: "No encrypted knowledge found for this skill." });
        }

        const record = knowledgeRecords[0];
        const encryptedDek = record.encryptedDek;
        const ciphertext = record.contentCiphertext;

        // Secure Pipeline
        const conclusion = await SecureBuffer.withSecureContext(
            async () => {
                // Fetch DEK securely from KMS
                return await KMS.decryptDek(encryptedDek);
            },
            async (dekBuffer) => {
                // We are now inside the Software Enclave / Sandbox
                // 1. Decrypt knowledge explicitly only in this scope
                const plaintextRules = KMS.decryptKnowledge(ciphertext as Buffer, dekBuffer);

                // 2. Mock RAG/Rule Engine processing
                // Instead of sending the full plaintextRules to an LLM, we resolve the score locally.
                const mockScore = Math.floor(Math.random() * 10) + 1;
                const generatedConclusion = `Evaluation Score: ${mockScore}/10. Match successful.`;

                // At the end of this scope, dekBuffer will be wiped by SecureBuffer
                return generatedConclusion;
            }
        );

        // Log the invocation (Auditing)
        const conclusionHash = crypto.createHash("sha256").update(conclusion).digest("hex");
        await fastify.db.insert(invocationLogsTable).values({
            id: randomUUID(),
            skillPackageId: packageId,
            apiClientId: "mock-agent-client-id", // Assume grabbed from Bearer token
            conclusionHash,
            createdAt: Date.now(),
        });

        // Firewall policy: We ONLY return the conclusion, never the original rules.
        return reply.send({
            success: true,
            conclusion,
            disclaimer: "Output is sanitized. Original rules were not leaked.",
        });
    });

    /**
     * CKKS Homomorphic Encryption PoC
     * Demonstrates computation on encrypted room data.
     */
    fastify.get("/ckks-poc", async (request, reply) => {
        const userScore = 85;
        const threshold = 15;

        // 1. Client encrypts data before sending
        const encScore = CKKSPoC.encrypt(userScore).ciphertext;
        const encThreshold = CKKSPoC.encrypt(threshold).ciphertext;

        // 2. Room processes encrypted data
        const encResult = CKKSPoC.processSecureRoom(encScore, encThreshold);

        return reply.send({
            concept: "Homomorphic Encryption (CKKS Simulation)",
            inputs: {
                userScore: "ENCRYPTED",
                threshold: "ENCRYPTED"
            },
            operation: "Addition over Ciphertexts",
            encryptedResult: encResult,
            message: "The room calculated the sum without ever seeing the numbers 85 or 15."
        });
    });
};

