import chokidar from "chokidar";
import path from "path";
import { logger } from "../lib/logger.js";
import { CliError } from "../lib/errors.js";
import { packPackage } from "../lib/packager.js";
import { SkillSandbox } from "../lib/sandbox.js";

/**
 * Dev Command
 */
export async function devCommand() {
    const cwd = process.cwd();

    logger.info("ðŸ”§  Starting YigYaps skill sandbox...");

    try {
        // Initial pack
        let payload = await packPackage(cwd);
        const sandbox = new SkillSandbox(payload);

        logger.success(`  âœ… Loaded ${payload.manifest.name} v${payload.manifest.version}`);
        logger.info(`  ðŸ“„ Rule files watched: ${payload.rules.length}`);
        console.log("\n  Test with: claude --mcp-server \"yigyaps dev-server\"");
        logger.hint("Use 'yigyaps dev-server' for standard-io based MCP client connections.");

        // Setup file watcher for hot-reloading
        const watcher = chokidar.watch([
            path.join(cwd, "package.json"),
            path.join(cwd, "rules/**/*"),
            path.join(cwd, "README.md")
        ], {
            ignoreInitial: true,
            persistent: true
        });

        watcher.on("change", async (filePath) => {
            const relativePath = path.relative(cwd, filePath);
            logger.info(`\nðŸ”„  File changed: ${relativePath}. Reloading...`);

            try {
                payload = await packPackage(cwd);
                sandbox.updatePayload(payload);
                logger.success(`  âœ… Reloaded ${payload.manifest.name}`);
            } catch (err: unknown) {
                const message = err instanceof Error ? err.message : String(err);
                logger.error(`  âŒ Failed to reload: ${message}`);
            }
        });

        // Keep process alive
        process.on("SIGINT", () => {
            watcher.close();
            logger.info("\nStopping sandbox...");
            process.exit(0);
        });

    } catch (error: any) {
        if (error instanceof CliError) throw error;
        throw CliError.system(`Sandbox failed to start: ${error.message}`);
    }
}

/**
 * Dev Server Command (StdIO mode for MCP clients)
 * This sits in the background when an MCP client connects.
 */
export async function devServerCommand() {
    try {
        const payload = await packPackage(process.cwd());
        const sandbox = new SkillSandbox(payload);
        await sandbox.start();
    } catch {
        process.exit(1);
    }
}

import { CliError } from "../lib/errors.js";
import { createRegistryClient } from "../lib/registry.js";
import { p } from "../lib/ui/prompts.js";
import { colors } from "../lib/ui/theme.js";
import { skillCard } from "../lib/ui/components.js";

interface InfoOptions {
    json?: boolean;
}

/**
 * Info Command
 */
export async function infoCommand(id: string, options: InfoOptions) {
    const s = p.spinner();

    try {
        const client = createRegistryClient();

        if (!options.json) {
            s.start(`Fetching details for '${id}'...`);
        }

        try {
            // Try by packageId first (more human friendly)
            let pkg;
            try {
                pkg = await client.getByPackageId(id);
            } catch {
                // Fallback to internal ID
                pkg = await client.getById(id);
            }

            if (!options.json) {
                s.stop("Skill details retrieved");
            }

            if (options.json) {
                console.log(JSON.stringify(pkg, null, 2));
                return;
            }

            // Render rich UI card
            console.log(skillCard(pkg as any));

            p.outro(`${colors.muted('To install this skill, run:')} ${colors.success(`yigyaps install ${pkg.packageId}`)}`);

        } catch {
            if (!options.json) s.stop("Skill not found");
            throw CliError.user(`Skill '${id}' not found in the registry.`);
        }
    } catch (error: unknown) {
        if (error instanceof CliError) throw error;
        const message = error instanceof Error ? error.message : String(error);
        throw CliError.system(`Info command failed: ${message}`);
    }
}

import { CliError } from "../lib/errors.js";
import { createRegistryClient } from "../lib/registry.js";
import { ensureAuthenticated } from "../lib/auth.js";
import { p, assertNotCancelled } from "../lib/ui/prompts.js";
import { colors } from "../lib/ui/theme.js";
import { skillCard } from "../lib/ui/components.js";

interface InstallOptions {
    agentId?: string;
    yes?: boolean;
}

/**
 * Install Command
 * 
 * Register a skill installation for an agent.
 */
export async function installCommand(id: string, options: InstallOptions) {
    p.intro(colors.primary.bold(`ðŸ“¥ Install Skill: ${id}`));
    const s = p.spinner();

    try {
        // 1. Ensure authenticated
        const user = await ensureAuthenticated();

        const client = createRegistryClient();

        // 2. Resolve package info
        s.start(`Checking skill '${id}' in registry...`);

        let pkg;
        try {
            try {
                pkg = await client.getByPackageId(id);
            } catch {
                pkg = await client.getById(id);
            }
            s.stop("Skill identified");
        } catch {
            s.stop("Skill not found");
            throw CliError.user(`Skill '${id}' not found in the registry.`);
        }

        // Show preview card
        console.log(skillCard(pkg as any));

        // 3. Confirm installation if no -y flag
        if (!options.yes) {
            const confirm = assertNotCancelled(await p.confirm({
                message: `Install '${pkg.displayName}' (v${pkg.version})?`,
                initialValue: true
            }));
            if (!confirm) {
                p.cancel("Installation aborted.");
                return;
            }
        }

        // 4. Get Agent ID
        let agentId = options.agentId;
        if (!agentId) {
            agentId = assertNotCancelled(await p.text({
                message: "Enter the Agent ID to install to:",
                placeholder: "yigbot_123...",
                validate: (v: string | undefined) => v ? undefined : "Agent ID is required",
            }));
        }

        // 5. Install
        s.start(`Installing to agent '${agentId}'...`);

        try {
            await client.install({
                packageId: pkg.id,
                yigbotId: agentId as string,
                userTier: user.tier,
            });

            s.stop(`Successfully installed ${colors.primary(pkg.displayName)} to ${colors.accent(agentId)}`);

            p.outro(`${colors.success("âœ¨ Installation Complete!")}

Next steps:
  ${colors.muted('â€¢')} Open YigYaps app to see the new skill
  ${colors.muted('â€¢')} Ask your agent about its new capabilities
  ${colors.muted('â€¢')} Details: ${colors.link(`https://yigyaps.com/skills/${pkg.id}`)}`);

        } catch (err: unknown) {
            s.stop("Installation failed");
            const message = err instanceof Error ? err.message : String(err);
            throw CliError.network(`Installation failed: ${message}`);
        }

    } catch (error: unknown) {
        if (error instanceof CliError) throw error;
        const message = error instanceof Error ? error.message : String(error);
        throw CliError.system(`Install command failed: ${message}`);
    }
}

import { p, assertNotCancelled } from "../lib/ui/prompts.js";
import { colors } from "../lib/ui/theme.js";
import { renderBanner } from "../lib/ui/banner.js";
import { searchCommand } from "./search.js";
import { listCommand } from "./list.js";
import { doctorCommand } from "./doctor.js";
import { whoamiCommand } from "./whoami.js";
import { loginCommand } from "./login.js";
import { logoutCommand } from "./logout.js";
import { getSession } from "../lib/auth.js";

/**
 * Interactive Main Menu
 */
export async function interactiveCommand() {
    renderBanner();

    const session = getSession();
    let isLoggedIn = !!session.apiKey;

    while (true) {
        const choice = assertNotCancelled(await p.select({
            message: "What would you like to do?",
            options: [
                { value: "browse", label: "ðŸ” Browse Skills", hint: "Search and explore the registry" },
                { value: "list", label: "ðŸ“¦ My Library", hint: "Show installed skills" },
                { value: "status", label: "âš¡ System Status", hint: "Run diagnostics & check connectivity" },
                { value: "account", label: isLoggedIn ? "ðŸ‘¤ My Profile" : "ðŸ”‘ Login", hint: isLoggedIn ? "View account and earnings" : "Sign in with GitHub" },
                { value: "exit", label: "ðŸšª Exit", hint: "Quit the CLI" },
            ] as any,
        })) as any;

        if (choice === "exit") break;

        switch (choice) {
            case "browse": {
                const query = await p.text({
                    message: "Search query (leave empty to browse latest):",
                    placeholder: "e.g. twitter, finance, tool..."
                });
                if (!p.isCancel(query)) {
                    await searchCommand(query || undefined, {});
                }
                break;
            }
            case "list": {
                await listCommand({});
                await p.note("Press enter to return to the main menu.");
                break;
            }
            case "status": {
                await doctorCommand();
                await p.note("Press enter to return to the main menu.");
                break;
            }
            case "account": {
                if (isLoggedIn) {
                    await whoamiCommand();
                    const subChoice = await p.select({
                        message: "Account actions:",
                        options: [
                            { value: "back", label: "â¬…ï¸ Back to menu" },
                            { value: "logout", label: "ðŸšª Logout", hint: "Sign out of your session" },
                        ]
                    });
                    if (subChoice === "logout") {
                        await logoutCommand();
                        isLoggedIn = false;
                    }
                } else {
                    await loginCommand({});
                    isLoggedIn = true;
                }
                break;
            }
        }

        // Pause before returning to menu
        if (choice !== "exit") {
            await p.note("Press enter to return to the main menu.");
        }
    }
}

