import Conf from 'conf';

export interface ConfigSchema {
    registryUrl: string;
    apiKey?: string;
    lastLogin?: string;
    firstRun?: boolean;
}

const config = new Conf<ConfigSchema>({
    projectName: 'yigyaps',
    defaults: {
        registryUrl: 'https://api.yigyaps.com',
        firstRun: true
    }
});

export const getConfig = () => config.store;
export const setConfig = <K extends keyof ConfigSchema>(key: K, value: ConfigSchema[K]) => {
    config.set(key, value);
};
export const updateConfig = (patch: Partial<ConfigSchema>) => {
    for (const [key, value] of Object.entries(patch)) {
        config.set(key as any, value);
    }
};
export const clearConfig = () => config.clear();
export const deleteConfig = <K extends keyof ConfigSchema>(key: K) => {
    config.delete(key);
};

import fs from "fs-extra";
import path from "path";
import { CliError } from "./errors.js";
import { manifestSchema } from "../commands/validate.js";

/**
 * Packager Helpers
 */

export interface RuleFile {
    path: string;
    content: string;
}

export interface PackagePayload {
    manifest: Record<string, any>;
    rules: RuleFile[];
    readme?: string;
}

/**
 * Reads the current directory and packs it into a payload.
 * Assumes valid structure (caller should run validate command first if unsure).
 */
export async function packPackage(cwd: string): Promise<PackagePayload> {
    const packageJsonPath = path.join(cwd, "package.json");
    if (!fs.existsSync(packageJsonPath)) {
        throw CliError.user("package.json not found in current directory.");
    }

    // 1. Read and parse package.json
    const rawManifest = await fs.readJson(packageJsonPath);
    const parsed = manifestSchema.safeParse(rawManifest);
    if (!parsed.success) {
        throw CliError.user("Invalid package.json manifest.");
    }
    const manifest = parsed.data;

    // 2. Read rules/ directory
    const rulesDir = path.join(cwd, "rules");
    if (!fs.existsSync(rulesDir)) {
        throw CliError.user("rules/ directory not found.");
    }

    const ruleFiles: RuleFile[] = [];
    const files = await fs.readdir(rulesDir);
    for (const file of files) {
        if (file.endsWith(".md") || file.endsWith(".txt")) {
            const content = await fs.readFile(path.join(rulesDir, file), "utf-8");
            ruleFiles.push({
                path: file,
                content
            });
        }
    }

    if (ruleFiles.length === 0) {
        throw CliError.user("No rule files (.md or .txt) found in rules/ directory.");
    }

    // 3. Read optional README.md
    let readme: string | undefined;
    const readmePath = path.join(cwd, "README.md");
    if (fs.existsSync(readmePath)) {
        readme = await fs.readFile(readmePath, "utf-8");
    }

    return {
        manifest,
        rules: ruleFiles,
        readme
    };
}

import updateNotifier from 'update-notifier';
import { brandGradient } from './ui/theme.js';
import boxen from 'boxen';

/**
 * Checks for package updates and notifies the user
 */
export async function checkForUpdates(pkg: any) {
    const notifier = updateNotifier({
        pkg,
        updateCheckInterval: 1000 * 60 * 60 * 24 // Check once a day
    });

    if (notifier.update) {
        const { latest, current } = notifier.update;

        const message =
            `Update available: ${brandGradient(current)} â†’ ${brandGradient.multiline(latest)}\n` +
            `Run ${brandGradient.multiline('npm install -g ' + pkg.name)} to update.`;

        console.log('\n' + boxen(message, {
            padding: 1,
            margin: 1,
            align: 'center',
            borderColor: 'yellow',
            borderStyle: 'round',
            title: 'âœ¦ Update Available âœ¦',
            titleAlignment: 'center',
        }));
    }
}

